typeProperties.script:
section Section1;
shared FilteredData = let AdfDoc = AzureStorage.BlobContents("https://stockagebth.blob.core.windows.net/ordersdata/data/filteredData.csv"),Csv = Csv.Document(AdfDoc, [Delimiter = ",", Encoding = TextEncoding.Utf8, QuoteStyle = QuoteStyle.Csv]), PromotedHeaders = Table.PromoteHeaders(Csv, [PromoteAllScalars = true]),
  #"Type de colonne changé" = Table.TransformColumnTypes(PromotedHeaders, {{"order_month", type datetime}}),
  #"Mois extrait" = Table.TransformColumns(#"Type de colonne changé", {{"order_month", each Date.Month(_), type nullable number}}),
  #"Type de colonne changé 1" = Table.TransformColumnTypes(#"Mois extrait", {{"quantity", Int64.Type}, {"unit_price", type number}}),
  #"Colonne personnalisée ajoutée" = Table.TransformColumnTypes(Table.AddColumn(#"Type de colonne changé 1", "total_order", each [unit_price] * [quantity]), {{"total_order", type number}}),
  #"Lignes groupées" = Table.Group(#"Colonne personnalisée ajoutée", {"order_month"}, {{"CA", each List.Sum([total_order]), type nullable number}}),
  #"Lignes triées" = Table.Sort(#"Lignes groupées", {{"order_month", Order.Ascending}}) in  #"Lignes triées";
shared UserQuery = let Source = #"FilteredData" in Source;
